mapper.select:
select

from metric


        status = #{status,jdbcType=TINYINT}


        and incident_code = #{incidentCode,jdbcType=VARCHAR}

mapper.insert:
insert into counter_metric (
    metric_code, metric_name, metric_type,
    incident_code, attribute_key, window_size, aggregation_type,
    status, description, create_time, update_time
) values (
             #{metricCode,jdbcType=VARCHAR}, #{metricName,jdbcType=VARCHAR}, #{metricType,jdbcType=TINYINT},
             #{incidentCode,jdbcType=VARCHAR}, #{attributeKey,jdbcType=VARCHAR}, #{windowSize,jdbcType=VARCHAR}, #{aggregationType,jdbcType=VARCHAR},
             #{status,jdbcType=TINYINT}, #{description,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
         )
